UPDATE locales_quest SET offerrewardtext_loc2='These handguards channel the power of the elements!' WHERE entry=9073;
UPDATE locales_quest SET offerrewardtext_loc2='Exellent $N !$B , you got everything just as i asked and now i can make a Earthshatter Girdle.' WHERE entry=9074;
UPDATE locales_quest SET offerrewardtext_loc2='The wristguards are ready, $N.$B$B<Rimblat bows.>' WHERE entry=9075;
UPDATE locales_quest SET offerrewardtext_loc2='You defeated him after all!  Undoubtedly my men softened him up for you.$B$BI jest, $C.  You\'ve done well.  You\'ll make a name for yourself if you keep your nose clean.' WHERE entry=9076;
UPDATE locales_quest SET offerrewardtext_loc2='Exellent $N !$B,you got everything just as i asked and now i can make a Bonescythe Breastplate.' WHERE entry=9077;
UPDATE locales_quest SET offerrewardtext_loc2='Be careful walking around in those things. You might cut yourself.' WHERE entry=9078;
UPDATE locales_quest SET offerrewardtext_loc2='Exellent $N !$B,you got everything just as i asked and now i can make a Bonescythe Helmet.' WHERE entry=9079;
UPDATE locales_quest SET offerrewardtext_loc2='<Rohan wipes the sweat from his brow.>$B$BThese shoulders took some work! Don\'t go out and get yourself killed like a scrub, $N.' WHERE entry=9080;
UPDATE locales_quest SET offerrewardtext_loc2='They go on your feet. Say, you haven\'t been nosing around in Tyr\'s Hand again, have you?' WHERE entry=9081;
UPDATE locales_quest SET offerrewardtext_loc2='These gauntlets could probably substitute as weapons if absolutely necessary. Wear them with pride!' WHERE entry=9082;
UPDATE locales_quest SET offerrewardtext_loc2='The belt is ready, $N. Have you ever seen such a high-quality item sold for so little?' WHERE entry=9083;
UPDATE locales_quest SET offerrewardtext_loc2='The bracers are ready, $N.$B$B<Rohan bows.>' WHERE entry=9084;
UPDATE locales_quest SET offerrewardtext_loc2='Your Dreamwalker Tunic is ready, $N.' WHERE entry=9086;
UPDATE locales_quest SET offerrewardtext_loc2='As promised, Dreamwalker Legguards.$B$B<Rayne hands you the armor.>' WHERE entry=9087;
UPDATE locales_quest SET offerrewardtext_loc2='Your Dreamwalker Headpiece is ready, $N.' WHERE entry=9088;
UPDATE locales_quest SET offerrewardtext_loc2='Your Dreamwalker Spaulders are ready, $N.' WHERE entry=9089;
UPDATE locales_quest SET offerrewardtext_loc2='Your Dreamwalker Boots are ready, $N.' WHERE entry=9090;
