UPDATE locales_quest SET offerrewardtext_loc2='Your Dreamwalker Wristguards are ready, $N.' WHERE entry=9093;
UPDATE locales_quest SET offerrewardtext_loc2='Exellent $N !,$Byou got everything just as i asked and now i can make a Frostfire Robe.' WHERE entry=9095;
