UPDATE locales_quest SET offerrewardtext_loc2='Heavy words, $N. If it were somebody else saying them, I\'d be likely to judge them insane.' WHERE entry=13363;
UPDATE locales_quest SET offerrewardtext_loc2='I do not view what we just went through as a failure, $N... quite the opposite.$B$BBy allowing me to peer into his heart, you allowed me to confirm what I needed to know. There is no Arthas Menethil anymore -- there is only the Lich King.' WHERE entry=13364;
