UPDATE locales_quest SET offerrewardtext_loc2='Nice shooting, $N. The Scourge never had a chance.$B$BAre you feeling ok? You look like you\'ve seen a ghost. Not that it would be an unusual thing around these parts, now that I think about it.' WHERE entry=13396;
