UPDATE locales_quest SET offerrewardtext_loc2='Hah, you got them! I could hear their scourge parts clatter off the mountain walls. Good work. We\'ll have more for you soon...' WHERE entry=13406;
UPDATE locales_quest SET offerrewardtext_loc2='You bring great honor to us, $N! The Alliance weaklings must be crushed under the heel of the mighty Horde!' WHERE entry=13407;
