UPDATE trans_words SET content_loc3='Rule #6: Do not polymorph inanimate objects! - We can\'t believe the Kirin\'Tor is receiving reports of this, but such a heinous act caused the development of the brand new Highly Severe Claims Disaster Case 1-A, "My Kingdom For A Horse: The One Hundred and Fifty Reasons Why A Kingdom Should Not Be Turned Into A Horse."$B$B<This goes on for hundreds of pages>' WHERE word_id = 126369;
