UPDATE trans_words SET content_loc3='Short range full-body instant cast teleportations have specific rules and boundaries which must be adhered to at all times. This book is a guideline for proper usage of this travel option, to ensure a safe and expedient teleportation.$B' WHERE word_id = 126370;
