UPDATE trans_words SET content_loc3='If this problem grows any further out of hand, I will be forced to insist that all portal travelers relinquish their weaponry and personal hygiene supplies before embarking. And the next time my lecture notes appear among the rubbish, I will personally transform the heads of all apprentices into dunce caps!
--Archmage Runeweaver 
' WHERE word_id = 127136;
