UPDATE `trans_words`SET `content_loc3`='You continue to honor the Alliance, $N. The Horde will be crushed before long if we keep our efforts up.' WHERE `word_id`=143464; -- locales_quest - offerrewardtext : entry=8383
UPDATE `trans_words`SET `content_loc3`='Good work, soldier! Keep it up and we\'ll have the Horde running all the way back to Orgrimmar.' WHERE `word_id`=143457; -- locales_quest - offerrewardtext : entry=8384
UPDATE `trans_words`SET `content_loc3`='You live up to yer reputation, $N. Without people like you, the Alliance wouldn\'t stand a chance against the savage forces of the Horde.' WHERE `word_id`=143467; -- locales_quest - offerrewardtext : entry=8385
UPDATE `trans_words`SET `content_loc3`='Well done, $N! You\'ve served the Alliance well.' WHERE `word_id`=143447; -- locales_quest - offerrewardtext : entry=8386
UPDATE `trans_words`SET `content_loc3`='Good work, $N! You should be proud of yourself. The Alliance invaders must be expelled from Alterac!' WHERE `word_id`=143470; -- locales_quest - offerrewardtext : entry=8387
UPDATE `trans_words`SET `content_loc3`='You continue to honor the Horde, $N. I\'m glad to see you haven\'t turned soft.' WHERE `word_id`=143473; -- locales_quest - offerrewardtext : entry=8388
UPDATE `trans_words`SET `content_loc3`='Excellent! You\'ve shown your worth by defending our operations in Warsong Gulch! May word of your honor spread far and wide across our lands.' WHERE `word_id`=143427; -- locales_quest - offerrewardtext : entry=8389
