UPDATE `trans_words`SET `content_loc3`='The battles of ages past are seeds from which legends grow. Yours is a time of heroes, young one. May your legend take root, thrive.$B$BI bid you well, $N, and offer you this token...' WHERE `word_id`=144070; -- locales_quest - offerrewardtext : entry=8635
