UPDATE `trans_words`SET `content_loc3`='This present looks like it has been shaken a few times. The tag on it reads:$B$BTo a very special $R $C.' WHERE `word_id`=144272; -- locales_quest - offerrewardtext : entry=8767
UPDATE `trans_words`SET `content_loc3`='This festively-wrapped present has your name on it.$B$BWait, did it just move?' WHERE `word_id`=144274; -- locales_quest - offerrewardtext : entry=8768
