UPDATE `trans_words`SET `content_loc3`='Ah, we were in need of these. Your work is appreciated, $N.' WHERE `word_id`=272121; -- locales_quest - offerrewardtext : entry=8782
UPDATE `trans_words`SET `content_loc3`='Ah, yes. These materials are of excellent quality. Thank you, $N.' WHERE `word_id`=272122; -- locales_quest - offerrewardtext : entry=8783
UPDATE `trans_words`SET `content_loc3`='An exceptional find, $N. Simply marvelous! This will definitely give us some insight into our enemy\'s twisted minds.' WHERE `word_id`=144329; -- locales_quest - offerrewardtext : entry=8784
UPDATE `trans_words`SET `content_loc3`='Ah, yes. Just what I needed! I\'ll make a special concoction with this. The silithid won\'t like it one bit!' WHERE `word_id`=144334; -- locales_quest - offerrewardtext : entry=8785
UPDATE `trans_words`SET `content_loc3`='Excellent work. We were in short supply of these, $N.' WHERE `word_id`=144338; -- locales_quest - offerrewardtext : entry=8786
