UPDATE `trans_words`SET `content_loc3`='These are precisely the materials I needed. Quite timely too, $N!$B$BIn mere moments I will have your distiller ready to go! In the meantime, take this as a reward for aiding me in my project.' WHERE `word_id`=144556; -- locales_quest - offerrewardtext : entry=8921
UPDATE `trans_words`SET `content_loc3`='Excellent! Just as I was running low!$B$BYou say Deliana sent you?' WHERE `word_id`=144561; -- locales_quest - offerrewardtext : entry=8922
UPDATE `trans_words`SET `content_loc3`='Excellent! Just as I was running low!$B$BYou say Mokvar sent you?' WHERE `word_id`=144564; -- locales_quest - offerrewardtext : entry=8923
UPDATE `trans_words`SET `content_loc3`='Yes, these will do great!$B$BBy tapping into the otherworldly energies contained within these substances we\'ll be able to reach out to those whose souls haven\'t quite left this world yet!' WHERE `word_id`=144569; -- locales_quest - offerrewardtext : entry=8924
UPDATE `trans_words`SET `content_loc3`='You\'ve done it! This will certainly provide enough energy to power up the extra-dimensional ghost revealer!' WHERE `word_id`=144574; -- locales_quest - offerrewardtext : entry=8925
UPDATE `trans_words`SET `content_loc3`='You\'ve kept your end of the bargain, I shall keep mine.$B$BJust remember that I\'m holding on to the best pieces until your work is finished.' WHERE `word_id`=144579; -- locales_quest - offerrewardtext : entry=8926
UPDATE `trans_words`SET `content_loc3`='Our agreement remains in place. Just remember, this is but a taste of what awaits you. Free me of this cursed fate and I will reward you with items of truly great power.' WHERE `word_id`=144583; -- locales_quest - offerrewardtext : entry=8927
UPDATE `trans_words`SET `content_loc3`='You did it! This rod will do perfectly. Now to put it all together...' WHERE `word_id`=144588; -- locales_quest - offerrewardtext : entry=8928
UPDATE `trans_words`SET `content_loc3`='Deliana had you contact me? I\'ll do my best to help you, but first I must ask a great favor of you.' WHERE `word_id`=144593; -- locales_quest - offerrewardtext : entry=8929
