UPDATE `trans_words`SET `content_loc3`='Look around you. Things aren\'t exactly in order.$B$BWe have a severe malfunction at the West Sanctum. Let us do our best to prevent matters from getting worse.' WHERE `word_id`=146254; -- locales_quest - offerrewardtext : entry=9119
UPDATE `trans_words`SET `content_loc3`='In all my days of living I would have never expected to see this...$B$B<Father Montoy appears to be salivating.>$B$BOh yes, you will be rewarded dear $G. You will be greatly rewarded. Give it here. Now!' WHERE `word_id`=272257; -- locales_quest - offerrewardtext : entry=9120
UPDATE `trans_words`SET `content_loc3`='You will feel a slight tingle, $N. That sensation means it\'s working. Once complete, you will be able to enter Naxxramas by stepping upon the rune portal in Plaguewood.' WHERE `word_id`=145044; -- locales_quest - offerrewardtext : entry=9121
