UPDATE `trans_words`SET `content_loc3`='Good day, $N. It was a pleasure doing business with you.' WHERE `word_id`=145199; -- locales_quest - offerrewardtext : entry=9228
UPDATE `trans_words`SET `content_loc3`='It is his ring! But it is destroyed. No other evidence of Ramaladni was found?$B$B<Korfax grimaces.>$B$BThis does not bode well for Ramaladni. The ring, however... it might be salvageable.' WHERE `word_id`=145225; -- locales_quest - offerrewardtext : entry=9229
UPDATE `trans_words`SET `content_loc3`='Ramaladni himself couldn\'t have done better! Here is the ring, as promised.' WHERE `word_id`=145229; -- locales_quest - offerrewardtext : entry=9230
UPDATE `trans_words`SET `content_loc3`='<Wilhelm wipes the sweat from his brow.>$B$BThat\'s some hard work! Enjoy the pants, $C. At least you\'ll die in a clean pair now.' WHERE `word_id`=145234; -- locales_quest - offerrewardtext : entry=9232
UPDATE `trans_words`SET `content_loc3`='He musta really been off his rocker when he wrote this! Omarion and I - we were thick as thieves!$B$BAnyhow, this certainly opens up some new possibilities, $N. I am now able to craft more items - for a small fee and material charges, of course.' WHERE `word_id`=145238; -- locales_quest - offerrewardtext : entry=9233
UPDATE `trans_words`SET `content_loc3`='Here is your order, $N. Delivered as promised!' WHERE `word_id`=145242; -- locales_quest - offerrewardtext : entry=9234
UPDATE `trans_words`SET `content_loc3`='Here is your order, $N. Delivered as promised!' WHERE `word_id`=145242; -- locales_quest - offerrewardtext : entry=9235
