UPDATE `trans_words`SET `content_loc3`='Honor the Dustwallow Marsh flame.' WHERE `word_id`=154652; -- locales_quest - offerrewardtext : entry=11847
UPDATE `trans_words`SET `content_loc3`='Honor the Feralas flame.' WHERE `word_id`=154653; -- locales_quest - offerrewardtext : entry=11849
UPDATE `trans_words`SET `content_loc3`='Honor the Hellfire Peninsula flame.' WHERE `word_id`=154654; -- locales_quest - offerrewardtext : entry=11851
UPDATE `trans_words`SET `content_loc3`='Honor the Hillsbrad Foothills flame.' WHERE `word_id`=154655; -- locales_quest - offerrewardtext : entry=11853
UPDATE `trans_words`SET `content_loc3`='Honor the Nagrand flame.' WHERE `word_id`=154656; -- locales_quest - offerrewardtext : entry=11854
