UPDATE `trans_words`SET `content_loc3`='It is good to know the people of this land still pay homage to the olden races. I bid you well, $N, and offer you this token...' WHERE `word_id`=144082; -- locales_quest - offerrewardtext : entry=13023
UPDATE `trans_words`SET `content_loc3`='Your spirit burns with life, young $C. I accept the homage you pay, and offer in return this token...' WHERE `word_id`=144108; -- locales_quest - offerrewardtext : entry=13024
UPDATE `trans_words`SET `content_loc3`='Your spirit burns with life, young $C. I accept the homage you pay, and offer in return this token...' WHERE `word_id`=144108; -- locales_quest - offerrewardtext : entry=13025
UPDATE `trans_words`SET `content_loc3`='Your spirit burns with life, young $C. I accept the homage you pay, and offer in return this token...' WHERE `word_id`=144108; -- locales_quest - offerrewardtext : entry=13026
