UPDATE `trans_words`SET `content_loc3`='Thanks, $N. Now, I can help the hostess out and still keep my word to Roberta.' WHERE `word_id`=164890; -- locales_quest - offerrewardtext : entry=14058
UPDATE `trans_words`SET `content_loc3`='Thanks for your help, and let me know if you ever get to the bottom of the case of the disappearing chutney.' WHERE `word_id`=164893; -- locales_quest - offerrewardtext : entry=14059
UPDATE `trans_words`SET `content_loc3`='These are great! Thank you for helping, $N.' WHERE `word_id`=164896; -- locales_quest - offerrewardtext : entry=14060
