UPDATE `trans_words`SET `content_loc3`='What a feast, hmmm?$B$BI\'m so glad you could celebrate the spirit of Pilgrim\'s Bounty with us.' WHERE `word_id`=164646; -- locales_quest - offerrewardtext : entry=14065
UPDATE `trans_words`SET `content_loc3`='These look a little worse for wear, but they\'re still edible. The crusaders will be glad to hear that we\'ve managed to recover some of the ship\'s cargo.' WHERE `word_id`=164652; -- locales_quest - offerrewardtext : entry=14074
UPDATE `trans_words`SET `content_loc3`='Well done, $N. I\'ll take these to the Argent Crusade\'s keepers right away. They\'ll have no choice but to acknowledge that we\'re doing far more than the Sunreavers to keep the coliseum running smoothly.' WHERE `word_id`=164657; -- locales_quest - offerrewardtext : entry=14076
UPDATE `trans_words`SET `content_loc3`='<Narasi accepts the prayer book from you.>$B$BThank you. The book was a gift from Confessor Paletress herself. She would be honored to know that you have used it to bring tranquility to the slain.' WHERE `word_id`=164662; -- locales_quest - offerrewardtext : entry=14077
UPDATE `trans_words`SET `content_loc3`='So, shall we begin your Apprentice Riding training?' WHERE `word_id`=164668; -- locales_quest - offerrewardtext : entry=14079
UPDATE `trans_words`SET `content_loc3`='Your actions have shown that we stand as one with the Argent Crusade, while the Sunreavers are too busy preening to take up their blades.' WHERE `word_id`=164673; -- locales_quest - offerrewardtext : entry=14080
