UPDATE `trans_words` SET `content_loc3`='Always good to see a semi-familiar face. You must be $N. Emissary Valustraa sent me word to expect you.$B$BThe people of Ashenvale need our assistance, $C. Speak with the citizens of Astranaar to see how you can help.' WHERE `word_id` = 146969; -- locales_quest - offerrewardtext : entry=9432
UPDATE `trans_words` SET `content_loc3`='Always more questions than answers, eh, $G lad:lass;?' WHERE `word_id` = 276681; -- locales_quest - offerrewardtext : entry=13207
UPDATE `trans_words` SET `content_loc3`='Am I truly laying my eyes upon the weapon of Thalorien Dawnseeker? This is a wondrous day for the Sunreavers and for all sin\'dorei!' WHERE `word_id` = 165067; -- locales_quest - offerrewardtext : entry=24564
UPDATE `trans_words` SET `content_loc3`='Amazing! From what little I\'ve seen of the fate of Farahlon, I\'m not certain I want to go outside, but it\'s much better than being trapped here for the rest of my days.' WHERE `word_id` = 150313; -- locales_quest - offerrewardtext : entry=10257
