UPDATE `trans_words` SET `content_loc3`='So it is true. They could not have chosen better timing for such an attack... between the Scourge and the blue flight, it is unlikely that anyone else has noticed yet.' WHERE `word_id` = 158734; -- locales_quest - offerrewardtext : entry=12148
