UPDATE `trans_words` SET `content_loc3`='Thanks. This looks great, and you\'re a fair bit faster than Jasper, but don\'t tell him I said so.' WHERE `word_id` = 164876; -- locales_quest - offerrewardtext : entry=14053
UPDATE `trans_words` SET `content_loc3`='Thankyeh, $C.$B$BWhen time allows I\'ll git up and have meself a look around.' WHERE `word_id` = 158592; -- locales_quest - offerrewardtext : entry=12114
