UPDATE db_script_string SET content_loc3='We wish to help you, spirit. We seek your wisdom.' WHERE entry=2000005493; /* content: We wish to help you, spirit. We seek your wisdom. */
UPDATE db_script_string SET content_loc3='Wisdom? It was your people that caused the insufferable wrath of Cyrukh.' WHERE entry=2000005494; /* content: Wisdom? It was your people that caused the insufferable wrath of Cyrukh. */
UPDATE db_script_string SET content_loc3='Why should we help you? The world is ruined. Destroyed.' WHERE entry=2000005495; /* content: Why should we help you? The world is ruined. Destroyed. */
UPDATE db_script_string SET content_loc3='%s nods.' WHERE entry=2000005496; /* content: %s nods. */
UPDATE db_script_string SET content_loc3='You are right, spirit. It was my people that did this to you. Allow us to make it right.' WHERE entry=2000005497; /* content: You are right, spirit. It was my people that did this to you. Allow us to make it right. */
UPDATE db_script_string SET content_loc3='Please tell us this: Who or what is Cyrukh?' WHERE entry=2000005498; /* content: Please tell us this: Who or what is Cyrukh? */
UPDATE db_script_string SET content_loc3='Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock.' WHERE entry=2000005499; /* content: Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock. */
UPDATE db_script_string SET content_loc3='The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool!' WHERE entry=2000005500; /* content: The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool! */
UPDATE db_script_string SET content_loc3='He is insane now. Driven mad during the shattering. There is no rest for the elements.' WHERE entry=2000005501; /* content: He is insane now. Driven mad during the shattering. There is no rest for the elements. */
UPDATE db_script_string SET content_loc3='There must be a way.' WHERE entry=2000005502; /* content: There must be a way. */
UPDATE db_script_string SET content_loc3='Yes, a way. Destroy Cyrukh. Destroy an elemental lord.' WHERE entry=2000005503; /* content: Yes, a way. Destroy Cyrukh. Destroy an elemental lord. */
UPDATE db_script_string SET content_loc3='Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him.' WHERE entry=2000005504; /* content: Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him. */
UPDATE db_script_string SET content_loc3='You must utter the words...' WHERE entry=2000005505; /* content: You must utter the words... */
UPDATE db_script_string SET content_loc3='Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master.' WHERE entry=2000005506; /* content: Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master. */
UPDATE db_script_string SET content_loc3='Yes... Gul\'dan...' WHERE entry=2000005507; /* content: Yes... Gul\'dan... */
UPDATE db_script_string SET content_loc3='We will find these words. We will summon your master and we will destroy him.' WHERE entry=2000005508; /* content: We will find these words. We will summon your master and we will destroy him. */
UPDATE db_script_string SET content_loc3='Talk. More talk... Now release us, mortal.' WHERE entry=2000005509; /* content: Talk. More talk... Now release us, mortal. */
UPDATE db_script_string SET content_loc3='Now to recover the lost words!' WHERE entry=2000005510; /* content: Now to recover the lost words! */
UPDATE db_script_string SET content_loc3='...to create a fund for the purchase of hair gel? I like my hair the way it is, thanks!' WHERE entry=2000005511; /* content: ...to create a fund for the purchase of hair gel? I like my hair the way it is, thanks! */
UPDATE db_script_string SET content_loc3='...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous!' WHERE entry=2000005512; /* content: ...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous! */
UPDATE db_script_string SET content_loc3='I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy...' WHERE entry=2000005513; /* content: I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy... */
UPDATE db_script_string SET content_loc3='...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense?' WHERE entry=2000005514; /* content: ...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense? */
UPDATE db_script_string SET content_loc3='That\'s the funniest thing I\'ve read in a long time. You\'ve just made my day!' WHERE entry=2000005515; /* content: That\'s the funniest thing I\'ve read in a long time. You\'ve just made my day! */
UPDATE db_script_string SET content_loc3='This... this is a joke, right?' WHERE entry=2000005516; /* content: This... this is a joke, right? */
UPDATE db_script_string SET content_loc3='Hey, thanks.' WHERE entry=2000005517; /* content: Hey, thanks. */
UPDATE db_script_string SET content_loc3='%s reads the leaflet.' WHERE entry=2000005518; /* content: %s reads the leaflet. */
UPDATE db_script_string SET content_loc3='...receive 50 percent off deserter undergarments? What is this garbage?' WHERE entry=2000005519; /* content: ...receive 50 percent off deserter undergarments? What is this garbage? */
UPDATE db_script_string SET content_loc3='What a bunch of lunatics! You actually believe this stuff?' WHERE entry=2000005520; /* content: What a bunch of lunatics! You actually believe this stuff? */
UPDATE db_script_string SET content_loc3='Thank you, $r! Your service to the Mag\'har will not be forgotten.' WHERE entry=2000005521; /* content: Thank you, $r! Your service to the Mag\'har will not be forgotten. */
UPDATE db_script_string SET content_loc3='Many Thanks, hero!' WHERE entry=2000005522; /* content: Many Thanks, hero! */
UPDATE db_script_string SET content_loc3='Spirits watch for you, $r.' WHERE entry=2000005523; /* content: Spirits watch for you, $r. */
UPDATE db_script_string SET content_loc3='Death to the ogres!' WHERE entry=2000005524; /* content: Death to the ogres! */
UPDATE db_script_string SET content_loc3='Thank you for freeing us, mortals. Beware, the Blue Flight is alerted to your presence. Even now, Malygos sends Varos Cloudstrider and his ring guardians to defend the Oculus. You will need our help to stand a chance.' WHERE entry=2000005525; /* content: Thank you for freeing us, mortals. Beware, the Blue Flight is alerted to your presence. Even now, Malygos sends Varos Cloudstrider and his ring guardians to defend the Oculus. You will need our help to stand a chance. */
UPDATE db_script_string SET content_loc3='Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands!' WHERE entry=2000005526; /* content: Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands! */
UPDATE db_script_string SET content_loc3='The trickster Mage-Lord Urom protects the third ring. He will appear alone and defenseless, but do not be fooled by appearances! Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures. Seek him out above.' WHERE entry=2000005527; /* content: The trickster Mage-Lord Urom protects the third ring. He will appear alone and defenseless, but do not be fooled by appearances! Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures. Seek him out above. */
UPDATE db_script_string SET content_loc3='Your greatest challenge lies ahead. Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring.' WHERE entry=2000005528; /* content: Your greatest challenge lies ahead. Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring. */
UPDATE db_script_string SET content_loc3='They who dine on lost souls know only hunger.' WHERE entry=2000005529; /* content: They who dine on lost souls know only hunger. */
UPDATE db_script_string SET content_loc3='Gaze into the void. It is the perpetuity in which they dwell.' WHERE entry=2000005530; /* content: Gaze into the void. It is the perpetuity in which they dwell. */
UPDATE db_script_string SET content_loc3='They drink your fear: it is the blood of life.' WHERE entry=2000005531; /* content: They drink your fear: it is the blood of life. */
UPDATE db_script_string SET content_loc3='They are the whisper on the shivering wind.' WHERE entry=2000005532; /* content: They are the whisper on the shivering wind. */
UPDATE db_script_string SET content_loc3='They do not die; they do not live. They are outside the cycle.' WHERE entry=2000005533; /* content: They do not die; they do not live. They are outside the cycle. */
UPDATE db_script_string SET content_loc3='Where one falls, many shall take its place.' WHERE entry=2000005534; /* content: Where one falls, many shall take its place. */
UPDATE db_script_string SET content_loc3='Thank you! I could not have held out for much longer.... A terrible thing has happened here.' WHERE entry=2000005535; /* content: Thank you! I could not have held out for much longer.... A terrible thing has happened here. */
UPDATE db_script_string SET content_loc3='We believed the Sanctum was well-fortified, but we were not prepared for the nature of this assault.' WHERE entry=2000005536; /* content: We believed the Sanctum was well-fortified, but we were not prepared for the nature of this assault. */
UPDATE db_script_string SET content_loc3='The Black Dragonkin materialized from thin air, and set upon us before we could react.' WHERE entry=2000005537; /* content: The Black Dragonkin materialized from thin air, and set upon us before we could react. */
UPDATE db_script_string SET content_loc3='We did not stand a chance. As my brethren perished around me, I managed to retreat here and bar the entrance.' WHERE entry=2000005538; /* content: We did not stand a chance. As my brethren perished around me, I managed to retreat here and bar the entrance. */
UPDATE db_script_string SET content_loc3='They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum.' WHERE entry=2000005539; /* content: They slaughtered us with cold efficiency, but the true focus of their interest seemed to be the eggs kept here in the sanctum. */
UPDATE db_script_string SET content_loc3='The commander of the forces on the ground here is a cruel brute named Zarithrian, but I fear there are greater powers at work.' WHERE entry=2000005540; /* content: The commander of the forces on the ground here is a cruel brute named Zarithrian, but I fear there are greater powers at work. */
UPDATE db_script_string SET content_loc3='In their initial assault, I caught a glimpse of their true leader, a fearsome full-grown twilight dragon.' WHERE entry=2000005541; /* content: In their initial assault, I caught a glimpse of their true leader, a fearsome full-grown twilight dragon. */
UPDATE db_script_string SET content_loc3='I know not the extent of their plans, heroes, but I know this:  They cannot be allowed to succeed!' WHERE entry=2000005542; /* content: I know not the extent of their plans, heroes, but I know this:  They cannot be allowed to succeed! */
UPDATE db_script_string SET content_loc3='Thanks, I shouldve never left Silverpine Forest.' WHERE entry=2000005543; /* content: Thanks, I shouldve never left Silverpine Forest. */
UPDATE db_script_string SET content_loc3='You have my thanks!' WHERE entry=2000005544; /* content: You have my thanks! */
UPDATE db_script_string SET content_loc3='Thank you. I thought I was going to die.' WHERE entry=2000005545; /* content: Thank you. I thought I was going to die. */
UPDATE db_script_string SET content_loc3='%s is chasing you!' WHERE entry=2000005546; /* content: %s is chasing you! */
UPDATE db_script_string SET content_loc3='Oh no! Here they come!' WHERE entry=2000005547; /* content: Oh no! Here they come! */
UPDATE db_script_string SET content_loc3='Ok, here I go!' WHERE entry=2000005548; /* content: Ok, here I go! */
UPDATE db_script_string SET content_loc3='What? How dare you say that to me?!?' WHERE entry=2000005549; /* content: What? How dare you say that to me?!? */
