UPDATE db_script_string SET content_loc3='Who dares step into my domain! Come! Come, and be consumed!' WHERE entry=2000005552; /* content: Who dares step into my domain! Come! Come, and be consumed! */
UPDATE db_script_string SET content_loc3='Let\'s move forward!' WHERE entry=2000005553; /* content: Let\'s move forward! */
UPDATE db_script_string SET content_loc3='...mumble...Petals of Fire...mumble...' WHERE entry=2000005554; /* content: ...mumble...Petals of Fire...mumble... */
UPDATE db_script_string SET content_loc3='...mumble mumble...' WHERE entry=2000005555; /* content: ...mumble mumble... */
UPDATE db_script_string SET content_loc3='...with the right mixture, perhaps...' WHERE entry=2000005556; /* content: ...with the right mixture, perhaps... */
UPDATE db_script_string SET content_loc3='...thorny vines...mumble...ouch!' WHERE entry=2000005557; /* content: ...thorny vines...mumble...ouch! */
UPDATE db_script_string SET content_loc3='Get out of here, there are too many of them! Escape while you can!' WHERE entry=2000005558; /* content: Get out of here, there are too many of them! Escape while you can! */
UPDATE db_script_string SET content_loc3='Help! Someone help us!' WHERE entry=2000005559; /* content: Help! Someone help us! */
UPDATE db_script_string SET content_loc3='Our friends fought well, Brann, but we\'re not done yet.' WHERE entry=2000005560; /* content: Our friends fought well, Brann, but we\'re not done yet. */
UPDATE db_script_string SET content_loc3='Perhaps so, but it\'s only a matter of time until we break back into Ulduar. Any luck finding a way to teleport inside?' WHERE entry=2000005561; /* content: Perhaps so, but it\'s only a matter of time until we break back into Ulduar. Any luck finding a way to teleport inside? */
UPDATE db_script_string SET content_loc3='None at all. I suspect it has something to do with that giant mechanical construct that our scouts spotted in front of the gate.' WHERE entry=2000005562; /* content: None at all. I suspect it has something to do with that giant mechanical construct that our scouts spotted in front of the gate. */
UPDATE db_script_string SET content_loc3='Oi. So we\'ll have to contend with that thing after all then?' WHERE entry=2000005563; /* content: Oi. So we\'ll have to contend with that thing after all then? */
UPDATE db_script_string SET content_loc3='What about the plated proto-drake and the fire giant that were spotted nearby? Think your mages can handle those?' WHERE entry=2000005564; /* content: What about the plated proto-drake and the fire giant that were spotted nearby? Think your mages can handle those? */
UPDATE db_script_string SET content_loc3='The Kirin Tor can\'t possibly spare any additional resources to take on anything that size. We may not have to though.' WHERE entry=2000005565; /* content: The Kirin Tor can\'t possibly spare any additional resources to take on anything that size. We may not have to though. */
UPDATE db_script_string SET content_loc3='We can sneak past them. As long as we can take down that construct in front of the gate, we should be able to get inside.' WHERE entry=2000005566; /* content: We can sneak past them. As long as we can take down that construct in front of the gate, we should be able to get inside. */
UPDATE db_script_string SET content_loc3='Sneak?! What do you think we are, marmots?' WHERE entry=2000005567; /* content: Sneak?! What do you think we are, marmots? */
UPDATE db_script_string SET content_loc3='We\'re hunting an old god, Brann.' WHERE entry=2000005568; /* content: We\'re hunting an old god, Brann. */
UPDATE db_script_string SET content_loc3='Fine. If our allies are going to be the ones getting their hands dirty, we\'ll leave it to them to decide how to proceed.' WHERE entry=2000005569; /* content: Fine. If our allies are going to be the ones getting their hands dirty, we\'ll leave it to them to decide how to proceed. */
UPDATE db_script_string SET content_loc3='What... What did you just do, $n?! Brann! Braaaaannn!' WHERE entry=2000005570; /* content: What... What did you just do, $n?! Brann! Braaaaannn! */
UPDATE db_script_string SET content_loc3='Brann! $n just activated the orbital defense system! If we don\'t get out of here soon, we\'re going to be toast!' WHERE entry=2000005571; /* content: Brann! $n just activated the orbital defense system! If we don\'t get out of here soon, we\'re going to be toast! */
UPDATE db_script_string SET content_loc3='Pentarus, you heard the man. Have your mages release the shield and let these brave souls through!' WHERE entry=2000005572; /* content: Pentarus, you heard the man. Have your mages release the shield and let these brave souls through! */
UPDATE db_script_string SET content_loc3='Of course, Brann: We will have the shield down momentarily.' WHERE entry=2000005573; /* content: Of course, Brann: We will have the shield down momentarily. */
UPDATE db_script_string SET content_loc3='Okay! Let\'s move out. Get into your machines; I\'ll speak to you from here via the radio.' WHERE entry=2000005574; /* content: Okay! Let\'s move out. Get into your machines; I\'ll speak to you from here via the radio. */
UPDATE db_script_string SET content_loc3='Mages of the Kirin Tor, on Brann\'s Command, release the shield! Defend this platform and our allies with your lives! For Dalaran!' WHERE entry=2000005575; /* content: Mages of the Kirin Tor, on Brann\'s Command, release the shield! Defend this platform and our allies with your lives! For Dalaran! */
UPDATE db_script_string SET content_loc3='Our allies are ready. Bring down the shield and make way!' WHERE entry=2000005576; /* content: Our allies are ready. Bring down the shield and make way! */
UPDATE db_script_string SET content_loc3='You\'ve defeated the Iron Council and unlocked the Archivum! Well done, lads!' WHERE entry=2000005577; /* content: You\'ve defeated the Iron Council and unlocked the Archivum! Well done, lads! */
UPDATE db_script_string SET content_loc3='We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis--' WHERE entry=2000005578; /* content: We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis-- */
UPDATE db_script_string SET content_loc3='I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him.' WHERE entry=2000005579; /* content: I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him. */
UPDATE db_script_string SET content_loc3='Hey! Stop rooting around in my cellar! Clear out!' WHERE entry=2000005580; /* content: Hey! Stop rooting around in my cellar! Clear out! */
UPDATE db_script_string SET content_loc3='What were you doing in my cellar? There\'s a food scare going on, and the last thing I need is strangers rummaging around in my goods! Shoo!' WHERE entry=2000005581; /* content: What were you doing in my cellar? There\'s a food scare going on, and the last thing I need is strangers rummaging around in my goods! Shoo! */
UPDATE db_script_string SET content_loc3='This whole situation seems a bit paranoid, don\'t you think?' WHERE entry=2000005582; /* content: This whole situation seems a bit paranoid, don\'t you think? */
UPDATE db_script_string SET content_loc3='Orders are orders. If the Prince says jump...' WHERE entry=2000005583; /* content: Orders are orders. If the Prince says jump... */
UPDATE db_script_string SET content_loc3='It\'s a strange order, you can\'t deny. Suspicious food? Under that definition, you should arrest Belfast!' WHERE entry=2000005584; /* content: It\'s a strange order, you can\'t deny. Suspicious food? Under that definition, you should arrest Belfast! */
UPDATE db_script_string SET content_loc3='I HEARD THAT! No more ale for you! Not a drop!' WHERE entry=2000005585; /* content: I HEARD THAT! No more ale for you! Not a drop! */
