UPDATE db_script_string SET content_loc3='%s grudgingly nods.' WHERE entry=2000005588; /* content: %s grudgingly nods. */
UPDATE db_script_string SET content_loc3='I can\'t argue with that.' WHERE entry=2000005589; /* content: I can\'t argue with that. */
UPDATE db_script_string SET content_loc3='Don\'t worry too much. By the time I went off duty, we hadn\'t found a scrap of befouled grain here.' WHERE entry=2000005590; /* content: Don\'t worry too much. By the time I went off duty, we hadn\'t found a scrap of befouled grain here. */
