UPDATE db_script_string SET content_loc3='Ok, enough work for now. Time for refreshments and a little conversation in the inn.' WHERE entry=2000005594; /* content: Ok, enough work for now. Time for refreshments and a little conversation in the inn. */
UPDATE db_script_string SET content_loc3='Wait, what\'s that smell?' WHERE entry=2000005595; /* content: Wait, what\'s that smell? */
