UPDATE db_script_string SET content_loc3='You don\'t mind me checking out your merchandise for signs of tampering, do you?' WHERE entry=2000005599; /* content: You don\'t mind me checking out your merchandise for signs of tampering, do you? */
UPDATE db_script_string SET content_loc3='No, Sergeant.' WHERE entry=2000005600; /* content: No, Sergeant. */
