UPDATE locales_npc_text SET Text0_0_loc9='Thank you again for helping Hilary, $g mister : miss;!  When I grow up, I wanna be a hero just like you!' WHERE entry=2277; /* Text0_0: Thank you again for helping Hilary, $g mister : miss;!  When I grow up, I wanna be a hero just like you! */
