UPDATE locales_npc_text SET Text0_1_loc9='You are eager, that is good. However, you are not yet ready to train with the likes of me.' WHERE entry=5246; /* Text0_1: You are eager, that is good. However, you are not yet ready to train with the likes of me. */
UPDATE locales_npc_text SET Text0_1_loc9='Your skills are truly becoming extraordinary. Now, let us continue your training...' WHERE entry=5247; /* Text0_1: Your skills are truly becoming extraordinary. Now, let us continue your training... */
UPDATE locales_npc_text SET Text0_0_loc9='Have you come to seek the knowledge of Hgarth?' WHERE entry=5248; /* Text0_0: Have you come to seek the knowledge of Hgarth? */
UPDATE locales_npc_text SET Text0_0_loc9='There is only one whose skills exceed my own. You are ready to receive her knowledge. She is Annora, and you may find her... in Uldaman.' WHERE entry=5249; /* Text0_0: There is only one whose skills exceed my own. You are ready to receive her knowledge. She is Annora, and you may find her... in Uldaman. */
