UPDATE locales_npc_text SET Text0_1_loc9='You have worked your way up, time to see the boss. Go to Josef and tell him you are ready.' WHERE entry=5557; /* Text0_1: You have worked your way up, time to see the boss. Go to Josef and tell him you are ready. */
UPDATE locales_npc_text SET Text0_1_loc9='How can I share my secrets with a beginner? Try speaking with Victor Ward first.' WHERE entry=5559; /* Text0_1: How can I share my secrets with a beginner? Try speaking with Victor Ward first. */
UPDATE locales_npc_text SET Text0_1_loc9='Good, you\'ve got some grit! I like that. Well then, let\'s get going!' WHERE entry=5560; /* Text0_1: Good, you\'ve got some grit! I like that. Well then, let\'s get going! */
