UPDATE locales_npc_text SET Text0_0_loc9='You\'ll need more training before I can share my knowledge with you, I\'m afraid.' WHERE entry=5564; /* Text0_0: You\'ll need more training before I can share my knowledge with you, I\'m afraid. */
UPDATE locales_npc_text SET Text0_0_loc9='Good! I can tell that my efforts will not be wasted!' WHERE entry=5565; /* Text0_0: Good! I can tell that my efforts will not be wasted! */
UPDATE locales_npc_text SET Text0_0_loc9='Cloth is okay. Personally I prefer to work with human skin.' WHERE entry=5566; /* Text0_0: Cloth is okay. Personally I prefer to work with human skin. */
UPDATE locales_npc_text SET Text0_0_loc9='You require the instruction of a true master! Go to Daryl Stack in Tarren Mill and embrace your destiny!' WHERE entry=5567; /* Text0_0: You require the instruction of a true master! Go to Daryl Stack in Tarren Mill and embrace your destiny! */
