UPDATE locales_npc_text SET Text0_0_loc9='Good! I can tell that my efforts will not be wasted!' WHERE entry=5570; /* Text0_0: Good! I can tell that my efforts will not be wasted! */
UPDATE locales_npc_text SET Text0_0_loc9='I have outfitted kings. With the proper training, you can too.' WHERE entry=5571; /* Text0_0: I have outfitted kings. With the proper training, you can too. */
UPDATE locales_npc_text SET Text0_0_loc9='You have come a long way, traveler. There is one more task for you to perform...' WHERE entry=5573; /* Text0_0: You have come a long way, traveler. There is one more task for you to perform... */
