UPDATE locales_npc_text SET Text0_1_loc9='Well done, soldier. A few more and our stables will be overflowing with mounts for the cavalry to ride into combat!' WHERE entry=6274; /* Text0_1: Well done, soldier. A few more and our stables will be overflowing with mounts for the cavalry to ride into combat! */
