UPDATE locales_npc_text SET Text0_1_loc9='Ahhhhggg... this pain in my head is killing me.$B$BThey must\'ve sent you to rescue me.  Took them long enough!  Do you have any idea of the things they do to prisoners here?' WHERE entry=8460; /* Text0_1: Ahhhhggg... this pain in my head is killing me.$B$BThey must\'ve sent you to rescue me.  Took them long enough!  Do you have any idea of the things they do to prisoners here? */
