UPDATE locales_npc_text SET Text0_0_loc9='Amongst the various pieces of parchment containing checklists and other information from the Farstriders, one stands out. It is a drawing of a hideous troll, perhaps the most fearsome creature that you have ever seen!' WHERE entry=8479; /* Text0_0: Amongst the various pieces of parchment containing checklists and other information from the Farstriders, one stands out. It is a drawing of a hideous troll, perhaps the most fearsome creature that you have ever seen! */
