UPDATE locales_npc_text SET Text0_0_loc9='I deal in comfort... Soothe your savage impulse, $r. My inn is your home... ' WHERE entry=8634; /* Text0_0: I deal in comfort... Soothe your savage impulse, $r. My inn is your home... */
