UPDATE locales_npc_text SET Text0_0_loc9='Greetings, $r.' WHERE entry=8828; /* Text0_0: Greetings, $r. */
UPDATE locales_npc_text SET Text0_0_loc9='It is a good day to be alive and one with the elements.' WHERE entry=8829; /* Text0_0: It is a good day to be alive and one with the elements. */
UPDATE locales_npc_text SET Text0_0_loc9='And why would I speak with you, $r?' WHERE entry=8830; /* Text0_0: And why would I speak with you, $r? */
UPDATE locales_npc_text SET Text0_0_loc9='Look around you, shaman.  Can you see the balance of the elements in the earth, the air, the water and the fire that has touched them all?' WHERE entry=8832; /* Text0_0: Look around you, shaman.  Can you see the balance of the elements in the earth, the air, the water and the fire that has touched them all? */
UPDATE locales_npc_text SET Text0_0_loc9='Don\'t be alarmed... while my gryphons may not be entirely alive, they will still carry you faithfully to your destination. Tell me... which tower do you seek?',Text0_1_loc9='Don\'t be alarmed... while my gryphons may not be entirely alive, they will still carry you faithfully to your destination. Tell me... which tower do you seek?',Text1_0_loc9='MISSING ENGLISH Text1:',Text1_1_loc9='MISSING ENGLISH Text1:' WHERE entry=8834; /* Text0_0: Don\'t be alarmed... while my gryphons may not be entirely alive, they will still carry you faithfully to your destination. Tell me... which tower do you seek? */ /* Text0_1: Don\'t be alarmed... while my gryphons may not be entirely alive, they will still carry you faithfully to your destination. Tell me... which tower do you seek? */ /* Text1_0: MISSING ENGLISH Text1: */ /* Text1_1: MISSING ENGLISH Text1: */
UPDATE locales_npc_text SET Text0_1_loc9='If you have not yet learned how to make use of bandages, I will be happy to show you the basics of creation and application. The difference between life and death can often come down to properly bandaging wounds.' WHERE entry=8835; /* Text0_1: If you have not yet learned how to make use of bandages, I will be happy to show you the basics of creation and application. The difference between life and death can often come down to properly bandaging wounds. */
UPDATE locales_npc_text SET Text0_0_loc9='I WILL have my revenge!' WHERE entry=8836; /* Text0_0: I WILL have my revenge! */
