UPDATE locales_npc_text SET Text0_0_loc9='Soon I will plant roots and sleep.   Before I rest there are things that must yet be done.' WHERE entry=10640; /* Text0_0: Soon I will plant roots and sleep.   Before I rest there are things that must yet be done. */
