UPDATE locales_npc_text SET Text0_1_loc9='Welcome to the Evergrove, $c. Timeon and I have been studying the arakkoa of Blade\'s Edge for some time now, and we\'ve found some alarming differences between them and their cousins in Terokkar.' WHERE entry=10793; /* Text0_1: Welcome to the Evergrove, $c. Timeon and I have been studying the arakkoa of Blade\'s Edge for some time now, and we\'ve found some alarming differences between them and their cousins in Terokkar. */
