UPDATE locales_npc_text SET Text0_0_loc9='Capturing the Sun\'s Reach Sanctum was the first step in our battle plan.  Our orders are to take control of the armory next.$B$BWe are $3233w percent done with our goal.' WHERE entry=12255; /* Text0_0: Capturing the Sun\'s Reach Sanctum was the first step in our battle plan.  Our orders are to take control of the armory next.$B$BWe are $3233w percent done with our goal. */
