UPDATE `locales_quest` SET `offerrewardtext_loc9`='You apply 4 Cenarion Plant Salves to the Night Dragon. It immediately begins to shed it\'s corrupted form, blossoming into a vibrant and healthy plant.' WHERE `entry`=4447;
