UPDATE `locales_quest` SET `offerrewardtext_loc9`='Take your trinket, fool. Leave me to my studies!' WHERE `entry`=4463;
UPDATE `locales_quest` SET `offerrewardtext_loc9`='You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.' WHERE `entry`=4464;
