UPDATE `locales_quest` SET `title_loc9`='Snapjaws, Mon!',`objectives_loc9`='Katoom the Angler at Revantusk Village in the Hinterlands wants you to kill 15 Saltwater Snapjaw turtles. Return to him when you have completed this task.',`offerrewardtext_loc9`='Oh, mon! I can\'t thank you enough! Hopefully this here fishing pole will be reward enough for the job.' WHERE `entry`=7815;
UPDATE `locales_quest` SET `title_loc9`='Gammerita, Mon!',`offerrewardtext_loc9`='MON! This is fantastic! With my best lure and the snapjaw population down, the fish will pile up.' WHERE `entry`=7816;
