UPDATE `locales_quest` SET `title_loc9`='Stalking the Stalkers',`objectives_loc9`='Huntsman Markhor at Revantusk Village in the Hinterlands wants you to kill 10 Silvermane Stalkers and 10 Silvermane Howlers. Return to him once the task is complete.$B$BMarkhor mentioned that the wolves hide in the wilds of the Hinterlands.',`offerrewardtext_loc9`='A job well done, $R. The Revantusk thank you for your contribution.' WHERE `entry`=7828;
UPDATE `locales_quest` SET `title_loc9`='Hunt the Savages',`objectives_loc9`='Huntsman Markhor at Revantusk Village in the Hinterlands wants you to kill 20 Savage Owlbeasts. Return to him once the task is complete.$B$BMarkhor mentioned that the Savage Owlbeasts occupy the wilds of the Hinterlands.',`offerrewardtext_loc9`='You have proven a worthy ally to the Revantusk, $N. Well done.' WHERE `entry`=7829;
