UPDATE `locales_quest` SET `offerrewardtext_loc9`='Ye did it! I don\'t know how, but ye did!' WHERE `entry`=13822;
UPDATE `locales_quest` SET `offerrewardtext_loc9`='Ye never cease to impress, $N.$B$BYe and me are having a pint together after all this is done, ye hear me?' WHERE `entry`=13823;
