UPDATE `locales_quest` SET `offerrewardtext_loc9`='You continue to prove yourself, $N. Accept this, not as a gift, but as a tool for the fight against the Scourge.' WHERE `entry`=24823;
UPDATE `locales_quest` SET `offerrewardtext_loc9`='You continue to prove yourself, $N. Accept this, not as a gift, but as a tool for the fight against the Scourge.' WHERE `entry`=24825;
