UPDATE db_script_string SET content_loc9='With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack.' WHERE entry=2000005271; /* content: With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack. */
