UPDATE db_script_string SET content_loc9='...and just look at where that got us!' WHERE entry=2000005278; /* content: ...and just look at where that got us! */
UPDATE db_script_string SET content_loc9='Even you must admit that your information from defectors is outdated and unreliable. Let us make calculated attacks on these manaforges and gather what information we can.' WHERE entry=2000005279; /* content: Even you must admit that your information from defectors is outdated and unreliable. Let us make calculated attacks on these manaforges and gather what information we can. */
UPDATE db_script_string SET content_loc9='Finally some talk of action from the big guy!' WHERE entry=2000005280; /* content: Finally some talk of action from the big guy! */
UPDATE db_script_string SET content_loc9='I can\'t belive you imbeciles went to school for this! Does anybody here know what they\'re doing?' WHERE entry=2000005281; /* content: I can\'t belive you imbeciles went to school for this! Does anybody here know what they\'re doing? */
UPDATE db_script_string SET content_loc9='Is it done yet? What is talking so damned long?' WHERE entry=2000005282; /* content: Is it done yet? What is talking so damned long? */
