UPDATE db_script_string SET content_loc9='Nice going you incompetent morons! Another one of our engineers vaporized!' WHERE entry=2000005293; /* content: Nice going you incompetent morons! Another one of our engineers vaporized! */
UPDATE db_script_string SET content_loc9='Thank you all for coming tonight. Now put your hands together to welcome Shattrath\'s best, Perry Gatner!' WHERE entry=2000005300; /* content: Thank you all for coming tonight. Now put your hands together to welcome Shattrath\'s best, Perry Gatner! */
UPDATE db_script_string SET content_loc9='Perry Gatner! What an act! The cantina is open all night long, so please, stay and enjoy yourselves!' WHERE entry=2000005301; /* content: Perry Gatner! What an act! The cantina is open all night long, so please, stay and enjoy yourselves! */
UPDATE db_script_string SET content_loc9='Check out the comedy stylings of Perry Gatner in just a few minutes!' WHERE entry=2000005302; /* content: Check out the comedy stylings of Perry Gatner in just a few minutes! */
UPDATE db_script_string SET content_loc9='If you are ready to laugh, head over to the World\'s End Tavern!' WHERE entry=2000005303; /* content: If you are ready to laugh, head over to the World\'s End Tavern! */
UPDATE db_script_string SET content_loc9='Now, in Shattrath City: The Tauren Chieftains! Playing in the World\'s End Tavern at the top of the hour!' WHERE entry=2000005304; /* content: Now, in Shattrath City: The Tauren Chieftains! Playing in the World\'s End Tavern at the top of the hour! */
UPDATE db_script_string SET content_loc9='Who? The Tauren Chieftains! What? A live performance. Where? The World\'s End Tavern! When? The top of the hour! BE THERE!' WHERE entry=2000005305; /* content: Who? The Tauren Chieftains! What? A live performance. Where? The World\'s End Tavern! When? The top of the hour! BE THERE! */
UPDATE db_script_string SET content_loc9='You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound!' WHERE entry=2000005306; /* content: You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound! */
UPDATE db_script_string SET content_loc9='Hmmm... Where is my fishing hook? Oh, there it is.' WHERE entry=2000005307; /* content: Hmmm... Where is my fishing hook? Oh, there it is. */
UPDATE db_script_string SET content_loc9='Let\'s see if the fish are biting.' WHERE entry=2000005308; /* content: Let\'s see if the fish are biting. */
UPDATE db_script_string SET content_loc9='Hmmm... I think I need better bait. I\'ll check my father\'s wagon.' WHERE entry=2000005309; /* content: Hmmm... I think I need better bait. I\'ll check my father\'s wagon. */
UPDATE db_script_string SET content_loc9='Quillboar scraps! These should do the trick. Fish love Quillboar.' WHERE entry=2000005310; /* content: Quillboar scraps! These should do the trick. Fish love Quillboar. */
UPDATE db_script_string SET content_loc9='Caught one! I\'ll see if any of the guards are hungry. Come on Fang!' WHERE entry=2000005311; /* content: Caught one! I\'ll see if any of the guards are hungry. Come on Fang! */
