UPDATE db_script_string SET content_loc9='A nice catch it is at that! You bring honor to the Horde boy, We thank you!' WHERE entry=2000005313; /* content: A nice catch it is at that! You bring honor to the Horde boy, We thank you! */
UPDATE db_script_string SET content_loc9='Would you like something more to eat sir?' WHERE entry=2000005314; /* content: Would you like something more to eat sir? */
UPDATE db_script_string SET content_loc9='More bread boy!' WHERE entry=2000005315; /* content: More bread boy! */
UPDATE db_script_string SET content_loc9='Right away sir.' WHERE entry=2000005316; /* content: Right away sir. */
UPDATE db_script_string SET content_loc9='Here is your bread sir.' WHERE entry=2000005317; /* content: Here is your bread sir. */
UPDATE db_script_string SET content_loc9='Aaahh... Very good. Now scat!' WHERE entry=2000005318; /* content: Aaahh... Very good. Now scat! */
UPDATE db_script_string SET content_loc9='As you wish sir.' WHERE entry=2000005319; /* content: As you wish sir. */
UPDATE db_script_string SET content_loc9='Can I go collect more rocks for my necklace, Papa?' WHERE entry=2000005320; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc9='Yes you may, Okla. But do not stray too far from home.' WHERE entry=2000005321; /* content: Yes you may, Okla. But do not stray too far from home. */
UPDATE db_script_string SET content_loc9='Dabu! I will be careful Papa!' WHERE entry=2000005322; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc9='Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it...' WHERE entry=2000005323; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc9='Hmm... That rock is nice, but too small.' WHERE entry=2000005324; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc9='This is a shiney rock. I\'ll put it in my bucket. Ok, I just need a few more...' WHERE entry=2000005325; /* content: This is a shiney rock. I\'ll put it in my bucket. Ok, I just need a few more... */
UPDATE db_script_string SET content_loc9='I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it.' WHERE entry=2000005326; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc9='There... That should be enough rocks to make a necklace. I should probably get home now anyway.' WHERE entry=2000005327; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc9='I brought you a housewarming gift, Jarl.' WHERE entry=2000005328; /* content: I brought you a housewarming gift, Jarl. */
UPDATE db_script_string SET content_loc9='Go on, open it. I think you\'ll like it.' WHERE entry=2000005329; /* content: Go on, open it. I think you\'ll like it. */
UPDATE db_script_string SET content_loc9='Jarl?' WHERE entry=2000005330; /* content: Jarl? */
UPDATE db_script_string SET content_loc9='Unless it\'s something I can eat, I don\'t want it.' WHERE entry=2000005331; /* content: Unless it\'s something I can eat, I don\'t want it. */
UPDATE db_script_string SET content_loc9='Jarl, you haven\'t been yourself ever since I arrived. What\'s going on?' WHERE entry=2000005332; /* content: Jarl, you haven\'t been yourself ever since I arrived. What\'s going on? */
UPDATE db_script_string SET content_loc9='Nothing. Wanna help me whip up some frog leg stew?' WHERE entry=2000005333; /* content: Nothing. Wanna help me whip up some frog leg stew? */
UPDATE db_script_string SET content_loc9='I came out here to visit you, Jarl! And I find you living in an empty cabin in the middle of nowhere, babbling about frogs? You\'re hopeless!' WHERE entry=2000005334; /* content: I came out here to visit you, Jarl! And I find you living in an empty cabin in the middle of nowhere, babbling about frogs? You\'re hopeless! */
UPDATE db_script_string SET content_loc9='And I am determined to find out more... So if you need my help again, you know where I\'ll be.' WHERE entry=2000005335; /* content: And I am determined to find out more... So if you need my help again, you know where I\'ll be. */
UPDATE db_script_string SET content_loc9='Anaya...? Do my eyes deceive me? Is it really you?' WHERE entry=2000005336; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc9='The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited.' WHERE entry=2000005337; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc9='That the fates should be so cruel as to permit us only this after a thousand years apart...' WHERE entry=2000005338; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc9='Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage.' WHERE entry=2000005339; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc9='Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more.' WHERE entry=2000005340; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc9='Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away...' WHERE entry=2000005341; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc9='No! Anaya... Anaya! Don\'t leave me! Please...' WHERE entry=2000005342; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc9='Farewell, Cerellean, until we are joined once again...' WHERE entry=2000005343; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc9='Anaya\'s soft voice trails away into the mists. "Know that I love you always..."' WHERE entry=2000005344; /* content: Anaya\'s soft voice trails away into the mists. "Know that I love you always..." */
UPDATE db_script_string SET content_loc9='How, my love? How will I find the strength to face the ages of the world without you by my side...' WHERE entry=2000005345; /* content: How, my love? How will I find the strength to face the ages of the world without you by my side... */
UPDATE db_script_string SET content_loc9='Thank you, $N. And luck to you.' WHERE entry=2000005346; /* content: Thank you, $N. And luck to you. */
UPDATE db_script_string SET content_loc9='Cliffspring Falls lies along the mountains to the east and north.' WHERE entry=2000005347; /* content: Cliffspring Falls lies along the mountains to the east and north. */
UPDATE db_script_string SET content_loc9='What is the meaning of this, Mor\'ghor?' WHERE entry=2000005348; /* content: What is the meaning of this, Mor\'ghor? */
UPDATE db_script_string SET content_loc9='SILENCE!' WHERE entry=2000005349; /* content: SILENCE! */
UPDATE db_script_string SET content_loc9='Blathering idiot. You incomprehensibly incompetent buffoon...' WHERE entry=2000005350; /* content: Blathering idiot. You incomprehensibly incompetent buffoon... */
UPDATE db_script_string SET content_loc9='THIS is your hero?' WHERE entry=2000005351; /* content: THIS is your hero? */
UPDATE db_script_string SET content_loc9='You have been deceived, imbecile.' WHERE entry=2000005352; /* content: You have been deceived, imbecile. */
UPDATE db_script_string SET content_loc9='This... whole... operation... HAS BEEN COMPROMISED!' WHERE entry=2000005353; /* content: This... whole... operation... HAS BEEN COMPROMISED! */
UPDATE db_script_string SET content_loc9='I expect to see this insect\'s carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death.' WHERE entry=2000005354; /* content: I expect to see this insect\'s carcass in pieces in my lair within the hour. Fail and you will suffer a fate so much worse than death. */
UPDATE db_script_string SET content_loc9='You will not harm the boy, Mor\'ghor! Quickly, $N, climb on my back!' WHERE entry=2000005355; /* content: You will not harm the boy, Mor\'ghor! Quickly, $N, climb on my back! */
UPDATE db_script_string SET content_loc9='%s begins to manipulate the Stone of Relu over the two fossils.' WHERE entry=2000005356; /* content: %s begins to manipulate the Stone of Relu over the two fossils. */
UPDATE db_script_string SET content_loc9='Who hath summoned forth Aman?' WHERE entry=2000005357; /* content: Who hath summoned forth Aman? */
UPDATE db_script_string SET content_loc9='Ah, I see you toil with relics of the past.' WHERE entry=2000005358; /* content: Ah, I see you toil with relics of the past. */
UPDATE db_script_string SET content_loc9='Be warned that even your creators are fallible.' WHERE entry=2000005359; /* content: Be warned that even your creators are fallible. */
UPDATE db_script_string SET content_loc9='Digging too deep into your past might bring an abrupt end to your future.' WHERE entry=2000005360; /* content: Digging too deep into your past might bring an abrupt end to your future. */
UPDATE db_script_string SET content_loc9='Aman dissipates before your eyes.' WHERE entry=2000005361; /* content: Aman dissipates before your eyes. */
UPDATE db_script_string SET content_loc9='Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted.' WHERE entry=2000005362; /* content: Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted. */
UPDATE db_script_string SET content_loc9='Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days.' WHERE entry=2000005363; /* content: Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days. */
UPDATE db_script_string SET content_loc9='Nothing surprising there.  Come back with some real news, will you?' WHERE entry=2000005364; /* content: Nothing surprising there.  Come back with some real news, will you? */
UPDATE db_script_string SET content_loc9='As expected.  Very well, dismissed.' WHERE entry=2000005365; /* content: As expected.  Very well, dismissed. */
UPDATE db_script_string SET content_loc9='By your leave, sir.' WHERE entry=2000005366; /* content: By your leave, sir. */
UPDATE db_script_string SET content_loc9='If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin.' WHERE entry=2000005367; /* content: If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin. */
UPDATE db_script_string SET content_loc9='The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him.' WHERE entry=2000005368; /* content: The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him. */
UPDATE db_script_string SET content_loc9='This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group.' WHERE entry=2000005369; /* content: This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group. */
UPDATE db_script_string SET content_loc9='Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker.' WHERE entry=2000005370; /* content: Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker. */
UPDATE db_script_string SET content_loc9='This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life.' WHERE entry=2000005371; /* content: This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life. */
UPDATE db_script_string SET content_loc9='If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus.' WHERE entry=2000005372; /* content: If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus. */
UPDATE db_script_string SET content_loc9='All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield.' WHERE entry=2000005373; /* content: All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield. */
UPDATE db_script_string SET content_loc9='Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter.' WHERE entry=2000005374; /* content: Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter. */
UPDATE db_script_string SET content_loc9='What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent.' WHERE entry=2000005375; /* content: What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent. */
UPDATE db_script_string SET content_loc9='When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed.' WHERE entry=2000005376; /* content: When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed. */
UPDATE db_script_string SET content_loc9='I doubt you have had much occasion to see such a creature. These demonic equines will make your travels much faster by acting as your mount as long as you control them. However, they are difficult to control, so be sure you are ready before attempting it.' WHERE entry=2000005377; /* content: I doubt you have had much occasion to see such a creature. These demonic equines will make your travels much faster by acting as your mount as long as you control them. However, they are difficult to control, so be sure you are ready before attempting it. */
UPDATE db_script_string SET content_loc9='There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around.' WHERE entry=2000005378; /* content: There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around. */
UPDATE db_script_string SET content_loc9='Phew! There\'s my lucky hat. I\'ve been looking for it everywhere.' WHERE entry=2000005379; /* content: Phew! There\'s my lucky hat. I\'ve been looking for it everywhere. */
UPDATE db_script_string SET content_loc9='I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here...' WHERE entry=2000005380; /* content: I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here... */
UPDATE db_script_string SET content_loc9='I can\'t possibly go out without my cloak. I hope it\'s in here...' WHERE entry=2000005381; /* content: I can\'t possibly go out without my cloak. I hope it\'s in here... */
